export default function Icons({ icons }) {
  return (
    <>
      {icons.map((icon) => {
        return (
          <div className="svg">
            {icon.svg}
            <span className="svg__icon-text">{icon.name}</span>
          </div>
        );
      })}

      <style jsx>{`
        .svg {
          text-align: center;
          margin: 30px;
        }
        .svg__icon-text {
          display: block;
          margin-top: 10px;
          padding: 0;
        }

        @media only screen and (max-width: 767px) {
          .svg {
            margin: 15px;
          }
        }
      `}</style>
    </>
  );
}

